import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface CrudPost {
  id?: number;
  name: string;
  comment: string;
}

@Injectable({
  providedIn: 'root'
})
export class CrudService {
  private apiUrl = '/crud';

  constructor(private http: HttpClient) { }

  getAllPosts(): Observable<CrudPost[]> {
    return this.http.get<CrudPost[]>(`${this.apiUrl}/fetch`);
  }

  getPostById(id: number): Observable<CrudPost> {
    return this.http.get<CrudPost>(`${this.apiUrl}/id/${id}`);
  }

  createPost(post: Omit<CrudPost, 'id'>): Observable<CrudPost> {
    return this.http.post<CrudPost>(`${this.apiUrl}/addCrud`, post);
  }

  updatePost(id: number, post: Omit<CrudPost, 'id'>): Observable<CrudPost> {
    return this.http.put<CrudPost>(`${this.apiUrl}/update/${id}`, post);
  }
}
