import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';

// If your custom components are standalone, import them here
import { Navbar } from './Navbar/navbar/navbar';     // adjust path if needed
import {App, Comment} from './comment/comment/comment'; // adjust path if needed
import { About } from './about/about';
import {Aboutus} from './comment/aboutus/aboutus';                   // adjust path/name as needed

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [
    RouterOutlet,
    Navbar,
    Comment,
    About,
    Aboutus
  ],
  template: `
    <navbar></navbar>
    <router-outlet></router-outlet>
    <comment></comment>
    <about></about>
  `,
  styles: []
})
export class App {
  // You can add properties/methods here if needed
}
